mapper.select:
        SELECT


--         级联表的字段

        FROM yimiao  a



                and a.id in

                #{item}



                and a.yimiao_name like CONCAT('%',#{params.yimiaoName},'%')


                and a.yimiao_types = #{params.yimiaoTypes}


                  and a.yimiao_number >= #{params.yimiaoNumberStart}   


                  and a.yimiao_number <= #{params.yimiaoNumberEnd}   


                and a.yimiao_number = #{params.yimiaoNumber}


                and a.yimiao_content like CONCAT('%',#{params.yimiaoContent},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        order by a.${params.orderBy} desc
