mapper.select:
        SELECT


--         级联表的字段

        FROM kouzhao  a



                and a.id in

                #{item}



                and a.kouzhao_name like CONCAT('%',#{params.kouzhaoName},'%')


                and a.kouzhao_types = #{params.kouzhaoTypes}


                  and a.kouzhao_number >= #{params.kouzhaoNumberStart}   


                  and a.kouzhao_number <= #{params.kouzhaoNumberEnd}   


                and a.kouzhao_number = #{params.kouzhaoNumber}


                and a.kouzhao_content like CONCAT('%',#{params.kouzhaoContent},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        order by a.${params.orderBy} desc
